mapper.sql:
SELECT a.jynd, a.jybh, a.ypbh, a.jybm,md.jymd_name as jymdmc, a.jyyj, aw.sqph, a.ypmc, a.spmc, a.pzwh, a.ph, f.currentstatus, f.sfmy, f.sfjsy, f.tjdx, a.sqlx, f.todoperson FROM sy_jymd md, sy_apply_wt aw, sy_apply ap, sy_weituo a
LEFT JOIN sy_flowdata f ON a.jybh = f.jybh
INNER JOIN sy_flowdata f ON a.jybh = f.jybh
AND f.currentstatus > 2
AND IFNULL(f.syqr,'1900-01-01') = '1900-01-01'
AND f.syqr is not null AND IFNULL(f.hsqr,'1900-01-01') = '1900-01-01' AND f.currentstatus > 6
AND f.hsqr is not null
AND f.syqr is not null
AND f.currentstatus = #{currentstatus}
AND f.currentstatus IN (9, 10, 99)
AND f.currentstatus IN (10, 99)
AND f.todoperson = #{userid}
AND f.sfmy = #{sfmy}
AND f.sfjsy = #{sfjsy}
AND f.tjdx = #{tjdx}
AND f.todoperson = #{todoperson}
INNER JOIN frame_employee e on e.userid=f.todoperson and e.name like '%${jyry}%'
WHERE a.inuse = 1 AND a.jymd = md.id and aw.ypbh = a.ypbh and ap.sqph=aw.sqph and ((ap.status = 9 and ap.iscjwt=0) OR ap.iscjwt=1)
AND a.jynd = ${jynd}
AND a.jybm = #{jybm}
AND (a.jybh IS NULL OR a.jybh='' or f.currentstatus = 2 )
AND aw.sqph=#{jyph}
AND (locate('${keyword}', a.jybh)>0 OR locate('${keyword}', a.ypmc)>0 OR locate('${keyword}', a.jyyj)>0 ) -- AND (a.jybh like '%${keyword}%' OR a.ypmc like '%${keyword}%' OR a.jyyj like '%${keyword}%')
AND (locate('${keyword}', a.jybh)>0 OR locate('${keyword}', a.ypmc)>0 OR locate('${keyword}', a.jyyj)>0 OR locate('${keyword}', a.scdw)>0) -- AND (a.jybh like '%${keyword}%' OR a.ypmc like '%${keyword}%' OR a.jyyj like '%${keyword}%' OR a.scdw like '%${keyword}%')
AND (locate('${keyword}', a.ypbh)>0 OR locate('${keyword}', a.ypmc)>0 OR locate('${keyword}', a.jyyj)>0 ) -- AND (a.ypbh like '%${keyword}%' OR a.ypmc like '%${keyword}%' OR a.jyyj like '%${keyword}%')

mapper.select:
SELECT w.id, w.jynd, w.jybh, w.ypbh, w.ypmc, w.spmc, w.jyxm, d4.name as jyxmmc, w.jyyj, w.jymd, x.sqph, x.jymdmc, w.jybm, org.`name` as jybmmc, w.yplx, d1.name as yplxmc, w.jx, d2.`name` as jxmc, w.bz, d3.`name` as bzmc, w.tyfb, CASE w.tyfb WHEN 1 THEN '同意' ELSE '不同意' END as tyfbmc, w.sfjj, CASE w.sfjj WHEN 1 THEN '加急' ELSE '普通' END as sfjjmc, w.qyfs, d5.`name` as qyfsmc, w.cjlx, d7.`name` as cjlxmc, w.cjyj, d8.`name` as cjyjmc, w.cjhj, d9.`name` as cjhjmc, w.gg, w.pzwh,w.ph, DATE_FORMAT(w.yxq, '%Y-%m-%d') as yxq, w.dbl, w.jpsl, DATE_FORMAT(w.cjrq, '%Y-%m-%d') as cjrq, w.scdw, w.scdwdz, w.scdwyb, w.scdwdh,w.scdwlxr, w.cjdw,w.sydw,w.wtdw,w.wtdwdz,w.wtdwyb,w.wtdwdh,w.wtdwcz,w.wtdwlxr,w.wtr, DATE_FORMAT(w.wtrq, '%Y-%m-%d') as wtrq,w.remark,w.inuse, w.sqlx, CASE w.sqlx WHEN 2 THEN '换发申请' ELSE '首次申请' END as sqlxmc, f1.sjr,DATE_FORMAT(f1.syqr, '%Y-%m-%d') as syqr,DATE_FORMAT(f1.jyqr, '%Y-%m-%d') as jyqr, e1.name as syr, DATE_FORMAT(f1.hsqr, '%Y-%m-%d') as hsqr,f1.hsqrr,f1.jysqrr,f1.ypcll,f1.sfmy, f1.sfjsy,f1.jyjl,f1.jsyjl, f1.tjdx, f1.todoperson, f1.jyrxm as jyr, f1.currentstatus,f1.sendto,f1.express, DATE_FORMAT(f1.postday, '%Y-%m-%d') as postday,IF(nullif(f1.yswj,'') != '', '已上传','') as yswj FROM (
ORDER BY aw.sqph DESC, a.ypbh ASC
ORDER BY a.jybh DESC
ORDER BY a.jybh ASC
LIMIT ${startindex}, ${stopindex}
) x INNER JOIN sy_weituo w ON w.ypbh = x.ypbh and x.jynd = w.jynd LEFT JOIN sy_flowdata f1 ON x.jybh = f1.jybh LEFT JOIN frame_organization org on w.jybm = org.id LEFT JOIN frame_dictionary d1 on w.yplx = d1.id LEFT JOIN frame_dictionary d2 on w.jx = d2.id LEFT JOIN frame_dictionary d3 on w.bz = d3.id LEFT JOIN frame_dictionary d4 on w.jyxm = d4.id LEFT JOIN frame_dictionary d5 on w.qyfs = d5.id LEFT JOIN frame_dictionary d7 on w.cjlx = d7.id LEFT JOIN frame_dictionary d8 on w.cjyj = d8.id LEFT JOIN frame_dictionary d9 on w.cjhj = d9.id LEFT JOIN frame_employee e1 on e1.userid=f1.sjr
